import React, { useState } from 'react';
import { BsEye, BsEyeSlash } from 'react-icons/bs';
import './Login.css';


const Login=()=>{
    const [nameData, setNameData] = useState('');
    const [emailData, setEmailData] = useState('');
    const[username,setUserName] = useState('');
    const[password, setPassword] = useState('');
    const[showPassword, setShowPassword] = useState(false);
    const[userCount, setUserCount] = useState(0);
    const [people, setPeople] = useState([]);
    

    const handlePasswordChange = (e)=>{
        const newPassword = e.target.value;
        // Password validation logic (12 digit, 1 special character, 1 lowercase, 1 uppercase, 1 number)
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{12,}$/;
        if(passwordRegex.test(newPassword)){
            setPassword(newPassword);
        }
    };

    const handleShowPassword = () => {
        setShowPassword(!showPassword);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (nameData && emailData && password) {
            const person = { id: new Date().getTime().toString(), nameData: nameData, emailData: emailData };
            setPeople((prevPeople) => [...prevPeople, person]);
            console.log("Form Submitted Successfully");
            setNameData('');
            setEmailData('');
            setPassword('');
        } else {
            console.log("Enter all details to submit");
        }
    };


    const handleAddUser = () =>{
        setUserCount(userCount+1);
        setUserName('');
        setPassword('');
        
    };
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>UserName:</label>
                <input type="text" id="email" name="email" value={emailData} onChange={(e)=>{setUserName(e.target.value)}}/>
                
                <label>Password:</label>
                <div className="password-input">
                    <input type={showPassword ? 'text': 'password'}
                    id="password"
                    name="password"
                    value={password}
                    onChange={handlePasswordChange}/>
                    {showPassword ? <BsEyeSlash onClick={handleShowPassword}/>:<BsEye onClick={handleShowPassword}/>}

                    
                </div>
                <button type="button" onClick={handleAddUser}>Add</button>
            </form>
            <div>
                <p>Number of users added: {userCount}</p>
            </div>
        </div>
    )

}

export default Login
