import React, { useEffect,useState } from 'react'


    const Cart = ({cart,setcart})=>{
        const [total,setTotal] = useState(0);

        useEffect(()=>{
            setTotal(cart.reduce((acc,curr)=>acc+Number(curr.Price)))
        })

    
   
  return (
    <div>
        <h1> Total Price: {total }</h1>
        {cart.map((p)=>{
	    return <oneProduct p={p} key={p.id} />
})}
        
      
    </div>
  )
}

export default Cart
