import React, {useState} from 'react'

const Multipleforms = () => {
    
        const [person, setPerson] = useState({firstName:'', emailData:'',age:''});
        const [people,setPeople]=useState([]);
        
        const handleSubmit =(e)=>{
        e.preventDefault();
        if(person.firstName && person.emailData && person.age){
        const p={...person, id: new Date().getTime().toString()}
        setPeople([...people,p]);
        setPerson({firstName:'', emailData:'',age:''})
        }
        }
        const handleChange=(e)=>{
        const name=e.target.name; //attribute - name (firstName,emailData)
        const value = e.target.value; //attribute -value (data we type from form)
        setPerson({...person,[name]:value}) ; //{firstName:'abc',emailData:'abc@gmail.com',age:8}
        }
       
        return(<>
        <form>
        <div>
        <label> Name </label>
        <input type="text" id="fname" name="firstName" value = {person.firstName} onChange={handleChange}/>
        </div>
        <div>
        <label> Email </label>
        <input type="text" id="email" name="emailData" value={person.emailData} onChange={handleChange}/>
        </div>
        <div>
        <label> Age </label>
        <input type="text" id="age" name="age" value={person.age} onChange={handleChange}/>
        </div>
        <button onClick={handleSubmit}> ADD </button>
        </form>
        {people.map((p)=>{
            const {id,nameData,emailData} = p;
                return(<div key={id}>
                        <h4> {nameData}  {emailData}</h4>
                    </div>)
            })}
        </>)
        }
        
    
export default Multipleforms
