import React, { useState } from 'react';
import { FaEye, FaEyeSlash } from 'react-icons/fa';
import './Login.css';


const Login=()=>{
    const [nameData, setNameData] = useState('');
    const [emailData, setEmailData] = useState('');
    const[username,setUserName] = useState('');
    const[password, setPassword] = useState('');
    const[showPassword, setShowPassword] = useState(false);
    const[userCount, setUserCount] = useState(0);
    const [people, setPeople] = useState([]);
    

    const handlePasswordChange = (e)=>{
        const newPassword = e.target.value;
        // Password validation logic (12 digit, 1 special character, 1 lowercase, 1 uppercase, 1 number)
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{12,}$/;
        if(passwordRegex.test(newPassword)){
            setPassword(newPassword);
        }
    };

    const handleShowPassword = () => {
        setShowPassword(!showPassword);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (nameData && emailData && password) {
            const person = { id: new Date().getTime().toString(), nameData: nameData, emailData: emailData };
            setPeople((prevPeople) => [...prevPeople, person]);
            console.log("Form Submitted Successfully");
            setNameData('');
            setEmailData('');
            setPassword('');
        } else {
            console.log("Enter all details to submit");
        }
    };


    const handleAddUser = () =>{
        setUserCount(userCount+1);
        setUserName('');
        setPassword('');
        
    };
    return (
        <div>
            <form onSubmit={handleSubmit}>
                
                <label>UserName:</label>
                <input type="text" id="email" name="email" value={username} onChange={(e)=>{setUserName(e.target.value)}}/>
                
                <label>Password:</label>
                {/* <div className="password-input">
                    <input type={showPassword ? 'text': 'password'}
                    id="password"
                    name="password"
                    value={password}
                    onChange={handlePasswordChange}/>
                    {showPassword ? <FaEyeSlash onClick={handleShowPassword}/>:<FaEye onClick={handleShowPassword}/>}

                    
                </div> */}
                <input 
                type="password"
                name="password"
                value={person.password}
                onChange={handlePasswordChange}
                className={isPasswordValid() ? 'valid' : 'invalid'}
            />
             <div className="password-icons">
                <span className={person.password && person.password.length >= 1 ? 'active' : ''}>ðŸ”‘</span>
                <span className={person.password && person.password.length >= 8 ? 'active' : ''}>ðŸ”’</span>
                <span className={isPasswordValid() ? 'active' : ''}>âœ…</span>
             </div>
                <button type="button" onClick={handleAddUser}>Add</button>
            </form>
            {people.map((p)=>{
            const {id,nameData,emailData,age} = p;
                return(<div key={id}>
                        <h4> {nameData}  {emailData}  {age}</h4>
                    </div>)
            })}
            <div>
                <p color='red'>Number of users added: {userCount}</p>
            </div>
        </div>
    )

}

export default Login
