const Book=(props)=>{
    console.log(props);

    function func(e){
        console.log(e);
        console.log(e.target);
    }

    const bookTitle = (title)=>{
        alert(`You selected the book with title ${title}`);
    }
    const {imgPath, title, author} = props;
    return (
        <article className='book'>
            <img src= {imgPath} alt="book" style={{width:'200px',height:'200px'}} onMouseOver={func} />
            <h1>{title}</h1>
            <i>{author}</i>
            <button className="u" onClick={()=>{alert(`You selected the book with the author ${author}`)}}>Author</button>
            <button className="s" onClick={()=>{bookTitle(title)}}>Title</button>
            {/* {5+6-4}
            {4*5 === 20? 'TRUE': 'FALSE' }
            {title.toUpperCase()} */}
        </article>
        );
    };
