        <form onSubmit={handleSubmit}>
        <div>
        <label> Name </label>
        <input type="text" id="fname" name="fname" />
        </div>
        <div>
        <label> Email </label>
        <input type="text" id="email" name="email" />
        </div>
        <button type="submit"> ADD </button>
        </form>
        </>)
        }

}

export default FromController;
