import React from "react";

const OneProducts = (props) => {
  console.log("Hi");
  const { p, cart, setcart } = props;
  const { id, title, author, imgPath, Price } = p;
  return (
    <div>
      <img
        src={imgPath}
        alt="book"
        style={{ width: "200px", height: "200px" }}
      />
      <h3>{title}</h3>
      <i>{author}</i>
      <b>{Price}</b>
      {!cart.includes(p) ? (
        <button
          onClick={() => {
            setcart([...cart, p]);
          }}
        >
          Add to Cart
        </button>
      ) : (
        <button
          onClick={() => {
            setcart(cart.filter((c) => c.id !== p.id));
          }}
        >
          Remove from cart
        </button>
      )}
    </div>
  );
};

export default OneProducts;
