import React, { useState } from 'react';
import { BsEye, BsEyeSlash } from 'react-icons/bs';
import './Login.css';


const Login=()=>{
    const[username,setUserName] = useState('');
    const[password, setPassword] = useState('');
    const[showPassword, setShowPassword] = useState(false);
    const[userCount, setUserCount] = useState(0);
    

    const handlePasswordChange = (e)=>{
        const newPassword = e.target.value;
        // Password validation logic (12 digit, 1 special character, 1 lowercase, 1 uppercase, 1 number)
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{12,}$/;
        if(passwordRegex.test(newPassword)){
            setPassword(newPassword);
        }
    };

    const handleAddUser = () =>{
        setUserCount(userCount+1);
        setUserName('');
        setPassword('');
        
    };
    return (
        <div>
            <form>
                <label>UserName:</label>
                <input type="text" value={username} onChange={(e)=>{setUserName(e.target.value)}}/>
                
                <label>Password:</label>
                <div className="password-input">
                    <input type={showPassword ? 'text': 'password'}
                    value={password}
                    onChange={handlePasswordChange}/>
                    {showPassword ? (
                        <BsEyeSlash onClick={()=>{setShowPassword(false)}}/>

                    ):(
                        <BsEye onClick={()=>{setShowPassword(true)}}/>
                    )}
                </div>
                
            </form>
        </div>
    )

}

export default Login
