import React, {useState} from 'react';
import {peoples} from './people';

var name = "Logesh";

const UStateBasics= () => {
  const [people, setPeople] = useState(people);
  const [crowd, setCrowd] = useState(people);
  var{fName,setfName} = useState('Logesh');

  const removeitem = (id)=>{
    var person = crowd.filter((person)=>{
      return person.id !== id;
    });
    setCrowd(person);

  }

  const changeofName = (n) =>{
    setfName("Robo");
    console.log(setfName);
  }

  const changeName = (n) =>{
    console.log(n);
  }

  return (
    
        <>
        {/* console.log(useState('Giri')); */}
        {peoples.map((person)=>{return <p>{person.name}</p>})}
        <button onClick={()=>{setPeople([])}}>Flushfully</button>
        {crowd.map((person)=>{return ( <div>
        <b>{person.name}</b>
        <button onClick={removeitem(person.id)}>FlushOne</button>
        </div>
        )})}
        <h1>{fName}</h1>
        <button onClick={()=>{changeofName(fName)}}>Change Fname useState</button>
        <h1>{name}</h1>
        <button onClick={()=>{changeName(name)}}>Change name Normal</button>
        </>
      
  )
}

export default UStateBasics;
