import './index.css';
import React from 'react';
import ReactDOM from 'react-dom';

var books = [
    {title:"Harry potter and the soccesser stone",author:"JK Rowling",imgPath: "https://m.media-amazon.com/images/I/51xJbFMRsxL.jpg"},
    {title:"Man's Search For Meaning: The classic tribute to hope from the Holocaust Paperback",author:"Victor E. Frankl",imgPath:"https://m.media-amazon.com/images/I/61nTspM+BYL._SY425_.jpg"},
    {title:"Turning Points : A Journey Through Challenges Paperback",author:"A.P.J.Abdul Kalam",imgPath:"https://m.media-amazon.com/images/I/81xiNxJ2mAL._SY425_.jpg"},
    {title:'',author:'',imgPath:''},
    {title:'',author:'',imgPath:''},
    {title:'',author:'',imgPath:''},
    {title:'',author:'',imgPath:''}
];


const BookList=()=>{
    return (

        <session className="booklist">
            <Book title = "Harry potter and the soccesser stone" author = "JK Rowling" imgPath = "https://m.media-amazon.com/images/I/51xJbFMRsxL.jpg"/>
            <Book title = "Man's Search For Meaning: The classic tribute to hope from the Holocaust Paperback" author = "Victor E. Frankl" imgPath = "https://m.media-amazon.com/images/I/61nTspM+BYL._SY425_.jpg"/>
            <Book title = "Turning Points : A Journey Through Challenges Paperback" author = "A.P.J.Abdul Kalam" imgPath = "https://m.media-amazon.com/images/I/81xiNxJ2mAL._SY425_.jpg" />
            <Book title = "The Complete Amazon Prime Guide Book - How to Get the Most out of Your Amazon Prime Membership Kindle Edition" author = "Gary Simmons" imgPath = "https://m.media-amazon.com/images/I/81WKzjK1cbL._SY425_.jpg" />
            <Book title = "The Pursuit of Happiness : A Book of Studies and Strowings Paperback" author = "Daniel G.Brinton" imgPath = "https://m.media-amazon.com/images/I/716f57zYnvL._SY425_.jpg" />
            <Book title = "The Girl in Room 105 Paperback" author = "Chetan Bhagat" imgPath = "https://m.media-amazon.com/images/I/51J+KYDiEYL._SY425_.jpg" />


        </session>
        );
    };


const Book=(props)=>{
    console.log(props);
    const {imgPath, title, author} = props
    return (
        <article className='book'>
            <img src= {imgPath} alt="book" style={{width:'200px',height:'200px'}} />
            <h1>{title}</h1>
            <i>{author}</i>
            {/* {5+6-4}
            {4*5 === 20? 'TRUE': 'FALSE' }
            {title.toUpperCase()} */}
        </article>
        );
    };
