import React, { useEffect, useState } from 'react'
import './Github.css';

const url = 'https://api.github.com/users';
const Github = () => {
    const [users,setUsers] = useState([]);
    const [search, setSearch] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState(false);
    const getUsers = async()=>{
        const response =await fetch(url);
        const data =await response.json();
        setUsers(data);
        // console.log(data);
    }
    useEffect(()=>{
        getUsers();
    },[]);

    const handleSubmit = (e) =>{
        e.preventDefault();
        
        var newResult = users.find((p)=>{
            return p.login === search
        });

        if(newResult){
            setResult(newResult);
        }else{
            setError(true);
        }
    }
  return (
    <div>
        <form onSubmit={handleSubmit}>
            <input type="text" name="search" className='bn' value={search} placeholder="Search for a github user" onChange={(e)=>{setSearch(e.target.value)}} />
            <button type='submit'>Search</button>
        </form>

            {/* {result&&<SearchedData result={result}/>}
            {error&&<h1 className='j'>Not Found</h1>} */}
            {result && (
                <div>
                    <h2>SearchedUser:</h2>
                    <img src={search.avatar_url} alt={search.login}></img>
                    <p>{search.login}</p>
                </div>
            )}
            
            <h2>Top 10 GitHub Users:</h2>
            <ul className='users'>
                {users.map((user) => (

                    <li key={user.id}>{user.login}
                    <img src={user.avatar_url} alt={user.login} />
                    </li>
                ))}
            </ul>

       
        {/* {users.map((data)=>{
            const {id,login,html_url,avatar_url} = data;
            return (
                <div key={id} className='card'>
                    <img src={avatar_url}  className='q' alt='Not Found'></img>
                    <div className='container'>
                        <a href={html_url} target='_blank'>{login}<h1 className='i'>{login}</h1></a>
                    </div>
                </div>
            )
        })} */}
      
    </div>
  );
};

